  case class UserReport(hoursWorked: Int, activeProjects: Int)

  case class User(firstName: String, lastName: String)

  object User {
    def validate(u: String): ValidatedList[String, User] = {
      val split = u.split('.')
      if (split.length == 2 && split(0).nonEmpty) {
        Valid(User(split(0), split(1)))
      } else {
        Invalid(List(s"Could not parse the String '$u' as a User"))
      }
    }

    def fetchReport(u: User): Future[UserReport] =
      Future.successful(u.firstName match {  // Just a mock, replace with real REST call in real life program
        case "john" => UserReport(120, 2)
        case "jane" => UserReport(160, 1)
        case "jason" => UserReport(200, 3)
        case "jonathan" => UserReport(70, 1)
        case "kevin" => UserReport(130, 1)
        case "barbara" => UserReport(220, 2)
        case "joanna" => UserReport(100, 1)
        case _ => UserReport(80, 1)
      })
  }


